import React, { Component } from 'react';
import PropTypes from "prop-types";
import isEqual from "react-fast-compare";
import  ImageMapper from 'react-image-mapper';
import  MaleBody  from '/Users/Danny/Documents/student-portal2/src/images/MaleBody.jpg';
import Modal from '../modal/Modal';


class Body extends Component {
	constructor() {
        super();

        this.state = {
            isShowing: false
        }
    }

    openModalHandler = () => {
        this.setState({
            isShowing: true
        });
    }

    closeModalHandler = () => {
        this.setState({
            isShowing: false
        });
    }

    render() {
       let  MAP ={
            name: "my-map",
            areas: [
              { name: "5", shape: "circle", coords: [136, 181, 3 ], preFillColor: "blue"   },
            ]
          }
        return (
            <div className="container" >
    <ImageMapper src={MaleBody} map={MAP} width={610} height={435} 
    	// onLoad={() => this.load()}
    	// // onClick={area => this.toggleModal(area)}
    	// onMouseEnter={area => this.enterArea(area)}
    	// onMouseLeave={area => this.leaveArea(area)}
    	// onMouseMove={(area, _, evt) => this.moveOnArea(area, evt)}
    	onImageClick={evt => this.openModalHandler(evt)}
    	// onImageMouseMove={evt => this.moveOnImage(evt)}
    />
     {
    	this.state.hoveredArea ? 
    	<span className="tooltip"
    	    style={{ ...this.getTipPosition(this.state.hoveredArea)}}>
    		{ this.state.hoveredArea && this.state.hoveredArea.name}
    	</span> : null
    }
     <button onClick={this.toggleModal}>
          Open the modal
        </button>
    <Modal show={this.state.isOpen}
          onClose={this.toggleModal}>
USED SPOT
    </Modal>
</div>


        );
    }
}
export default Body;
