import React, { Component } from 'react';
import  ImageMapper from 'react-image-mapper';
import  MaleBody  from '/Users/Danny/Documents/student-portal2/src/images/MaleBody.jpg';
import Modal from '../modal/Modal';
import axios from 'axios';
import Timer from 'react-compound-timer'


class Body extends Component {
	constructor() {
        super();


        this.state = {
			isShowing: false,
			preFillColor: "white",
			areas: [],
			selectedArea: undefined

		}

    }



    openModalHandler = (area) => {
		console.log("selectedArea ", area)
        this.setState({
			isShowing: true,
			selectedArea: area
		});
    }


    closeModalHandler = () => {
        this.setState({
            isShowing: false
        });
	}

	updateArea = (evt) => {
		let newArea = { name: "1", shape: "circle", coords: [evt.nativeEvent.layerX, evt.nativeEvent.layerY , 4 ], fillColor: this.state.preFillColor, preFillColor: this.state.preFillColor  }
		// const areas = this.state.areas
		this.setState(
			{
				areas: [...this.state.areas, newArea],
				isShowing: true,
				selectedArea: newArea
			}
		)

    }

	areasSubmitHandler = (event) => {
		// event.preventDefault();
		console.log("area ::> ",this.state.selectedArea)
        axios.post('http://localhost:8080/sites/add-site?studentID=',{
			xCoord: this.state.selectedArea.coords[0],
			yCoord: this.state.selectedArea.coords[1],
			shape: this.state.selectedArea.shape,
			fillColor: this.state.selectedArea.fillColor,
			siteStudent: this.state.loggedInStudent
		})
        .then(response => {
            //navigate to a thank you page
        }).catch(error => {
            // error handling goes here
        })



    }

	beginSiteTracker = () => {

		this.setState({ preFillColor: "green"		

	});


	}


    render() {
		console.log(this.state)



       let  MAP ={
			name: "my-map",
			areas: this.state.areas
            // areas: [
            //   { name: "1", shape: "circle", coords: [132, 181, 3 ], fillColor: this.state.preFillColor, preFillColor: this.state.preFillColor  },
			//   { name: "2", shape: "circle", coords: [145, 181, 3 ], fillColor: this.state.preFillColor, preFillColor: this.state.preFillColor  },
			//   { name: "3", shape: "circle", coords: [191, 182, 3 ], fillColor: this.state.preFillColor, preFillColor: this.state.preFillColor  },

			// ]
          }


		  return (

			<div>
			{ this.state.isShowing ? <div onClick={this.closeModalHandler} ></div> : null }


				<Modal
				show={this.state.isShowing}
				close={this.closeModalHandler}
				beginSiteTracker={this.beginSiteTracker}
				areasSubmitHandler={this.areasSubmitHandler}

				>

					Do you want to confirm this as your current infusion site location?

					</Modal>
			}
		<div className="imagemap">
					<ImageMapper src={MaleBody} fillColor={this.state.preFillColor} map={MAP} width={610} height={435} 
	button className="open-modal-btn" 

	onClick={(area) => this.openModalHandler(area)}
	onImageClick={(evt) => this.updateArea(evt)}

    >


			</ImageMapper>
			</div>
    </div>







);
}
}


export default Body;
