import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import axios from 'axios';

class Header extends Component {
    state = {
        student : {
            email: '',
            password: ''
        }
    }
    signInSubmitHandler = (event) => {
        // preventing query string from displaying on the browser
        event.preventDefault();

//********************Start*********************/

        //Only if your login backend is not working
        // localStorage.setItem("loggedInUser", "john.doe@email.com");
        // this.props.history.push('/');
//********************END*********************/

        axios.post('http://localhost:8080/login',this.state.student)
        .then(response => {
           //Only if your login backend is not working
            // localStorage.setItem("loggedInUser", response.data.email);
            this.props.history.push('/');
            //navigate to a home page
        }).catch(error => {
            // error handling goes here
        })

    }
    signInChangeHandler = (event) => {
       const key =  event.target.name;
       const value = event.target.value;
    // making a copy of the student object in the state
       const tempStudent = {...this.state.student};
       tempStudent[key] = value;
       this.setState( {
           student: tempStudent
       })

    }
    signOut = () => {
        localStorage.removeItem('loggedInUser');
        this.props.history.push('/');
    }
    render() {
        let dropDownList = (
            <li className="nav-item ">
                <Link className="nav-link" to="/sign-up">Sign Up  </Link>
            </li>
        )
        let signInSignOutForm = (
            <form onSubmit={this.signInSubmitHandler} className="form-inline mt-2 mt-md-0">
                <input onChange={this.signInChangeHandler} value={this.state.student.email} name="email" className="form-control mr-sm-2" type="text" placeholder="Email" aria-label="Email" />
                <input onChange={this.signInChangeHandler} value={this.state.student.password} name="password" className="form-control mr-sm-2" type="password" placeholder="Password" aria-label="Password" />
                <button className="btn btn-outline-success my-2 my-sm-0" type="submit">Sign In</button>
            </form>
        )
        if(localStorage.getItem('loggedInUser')){
            dropDownList = (
                <li className="nav-item ">
                    <Link className="nav-link" to="/settings">Settings  </Link>
                </li>
            )
            signInSignOutForm =(
                <div  className="form-inline mt-2 mt-md-0">
                <button onClick={this.signOut} className="btn btn-outline-success my-2 my-sm-0" type="submit">Sign Out</button>
            </div>
            )
        }
        return (
            <div className="margin-bottom-50px">
                <nav className="navbar navbar-expand-md navbar-dark fixed-top bg-dark">
                    <Link className="navbar-brand" to="/">Student Portal</Link>
                    <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarCollapse" aria-controls="navbarCollapse" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarCollapse">
                        <ul className="navbar-nav mr-auto">
                            <li className="nav-item active">
                                <Link className="nav-link" to="/about-us">About-us <span className="sr-only">(current)</span></Link>
                            </li>

                                     {dropDownList}

                        </ul>
                        {signInSignOutForm}
                    </div>
                </nav>
            </div>
        );
    }
}

export default Header;
