import React, {Component} from 'react';
import countdown from 'countdown'
import './Modal.css';


class Modal extends Component {

    state = {
        timer: ""
    }

    componentDidMount(){
        setInterval(
            () => this.setState({timer: countdown(new Date(), expireDate, countdown.HOURS|countdown.MINUTES|countdown.SECONDS).toString()}),
            1000
        )
    }

    cancel = (event) => {
        this.props.removeLastAddedArea()
    }


    confirmSite = (event) => {
        this.props.beginSiteTracker()
        this.props.areasSubmitHandler()
        this.props.close()

    };

    render(){
        console.log("Timer props -> ", this.props.selectedArea.date)
        let initialDate  = new Date(this.props.selectedArea.date)
        let expireDate = new Date(initialDate)
        expireDate.setDate(initialDate.getDate()+3);
        console.log("expire date ::> ", expireDate)

        let modalMessage = (
            <div></div>

        )

        if (this.props.selectedArea.savedArea=true){
            console.log(this.props.selectedArea.savedArea)
            console.log(this.props.selectedArea)
            modalMessage=(
                <div>
                <div className="modal-wrapper"
                    style={{
                        transform: this.props.show ? 'translateY(0vh)' : 'translateY(-100vh)',
                        opacity: this.props.show ? '1' : '0'
                    }}>
                    <div className="modal-header">
                        <h3>Infusion Tracker</h3>
                        <span className="close-modal-btn" onClick={this.cancel}>Ã—</span>
                    </div>

                    <div className="modal-body">
                        <p>This site expires on:</p>
                    {   
                        this.state.timer
                    }


                    </div> 
                    <div className="modal-footer">
                    <button className="btn-cancel" onClick={this.props.close}>Close</button>
                    </div>
                    </div>
                </div>

            )

        }
        if(this.props.selectedArea.savedArea=false)
        {
            modalMessage =( 
                <div>
                    <div className="modal-wrapper"
                    style={{
                        transform: this.props.show ? 'translateY(0vh)' : 'translateY(-100vh)',
                        opacity: this.props.show ? '1' : '0'
                    }}></div>
            <div className="modal-body">
                    <p>
                        {this.props.children}
                        Do you want to confirm this as your current infusion site?
                    </p>
                    <div className="modal-header">
                        <h3>Infusion Tracker</h3>

                    </div>
                </div>
                <div className="modal-footer">
                    <button className="btn-cancel" onClick={this.cancel}>Cancel</button>
                    <button className="btn-continue" onClick={this.confirmSite}>Confirm</button>
                </div>
                </div>
            )
        }


        return (
            <div>

                    {modalMessage}


                </div>

        );
    }

}

export default Modal;
