import React, { Component } from 'react';
import PropTypes from "prop-types";
import isEqual from "react-fast-compare";
import  ImageMapper from 'react-image-mapper';
import  MaleBody  from '/Users/Danny/Documents/student-portal2/src/images/MaleBody.jpg';


class Body extends Component {
   state = {
    hoveredArea: null
   }
    enterArea= (area) =>{
        this.setState({ hoveredArea: area });
    }

    leaveArea=(area) =>{
        this.setState({ hoveredArea: null });
    }

    getTipPosition=(area) =>{
        return { top: `${area.center[1]}px`, left: `${area.center[0]}px` };
    }
    getInitialState=() =>{
		return { hoveredArea: null, msg: null, moveMsg: null };
	}
	load=() =>{
		this.setState({ msg: "Interact with image !" });
	}
	clicked=(area) =>{
		this.setState({
			msg: `You clicked on ${area.shape} at coords ${JSON.stringify(
				area.coords
			)} !`
		});
	}
	clickedOutside=(evt) =>{
		const coords = { x: evt.nativeEvent.layerX, y: evt.nativeEvent.layerY };
		this.setState({
			msg: `You clicked on the image at coords ${JSON.stringify(coords)} !`
		});
	}
	moveOnImage=(evt) =>{
		const coords = { x: evt.nativeEvent.layerX, y: evt.nativeEvent.layerY };
		this.setState({
			moveMsg: `You moved on the image at coords ${JSON.stringify(coords)} !`
		});
	}
	enterArea = (area) => {
		this.setState({ hoveredArea: area });
	}
	leaveArea = (area) =>{
		this.setState({
			hoveredArea: null,
			msg: `You leaved ${area.shape} ${area.name} at coords ${JSON.stringify(
				area.coords
			)} !`
		});
	}
	moveOnArea=(area, evt) =>{
		const coords = { x: evt.nativeEvent.layerX, y: evt.nativeEvent.layerY };
		this.setState({
			moveMsg: `You moved on ${area.shape} ${
				area.name
			} at coords ${JSON.stringify(coords)} !`
		});
	}

	getTipPosition=(area) =>{
		return { top: `${area.center[1]}px`, left: `${area.center[0]}px` };
	}
    render() {
       let  MAP ={
            name: "my-map",
            areas: [

              { name: "5", shape: "circle", coords: [131, 178, 5 ], preFillColor: "blue" },
            ]
          }
        return (
            <div className="container" >
    <ImageMapper src={MaleBody} map={MAP} width={585} 
    	onLoad={() => this.load()}
    	onClick={area => this.clicked(area)}
    	onMouseEnter={area => this.enterArea(area)}
    	onMouseLeave={area => this.leaveArea(area)}
    	onMouseMove={(area, _, evt) => this.moveOnArea(area, evt)}
    	onImageClick={evt => this.clickedOutside(evt)}
    	onImageMouseMove={evt => this.moveOnImage(evt)}
    />
     {
    	this.state.hoveredArea ? 
    	<span className="tooltip"
    	    style={{ ...this.getTipPosition(this.state.hoveredArea)}}>
    		{ this.state.hoveredArea && this.state.hoveredArea.name}
    	</span> : null
    }
</div>


        );
    }
}
export default Body;
