import React from 'react';
import './Modal.css';

import { Timer } from 'react-compound-timer';



const Modal = (props) => {




    let confirmSite = (event) => {
            props.beginSiteTracker()
            props.areasSubmitHandler()
            props.close()

		};
    return (

        <div>
            <div className="modal-wrapper"
                style={{
                    transform: props.show ? 'translateY(0vh)' : 'translateY(-100vh)',
                    opacity: props.show ? '1' : '0'
                }}>
                <div className="modal-header">
                    <h3>Infusion Tracker</h3>
                    <span className="close-modal-btn" onClick={props.close}>Ã—</span>
                </div>
                <div className="modal-body">
                    <p>
                    <Timer
    initialTime={55000}
    direction="backward"
>
    {() => (
        <React.Fragment>
            <Timer.Days /> days
            <Timer.Hours /> hours
            <Timer.Minutes /> minutes
            <Timer.Seconds /> seconds
            <Timer.Milliseconds /> milliseconds
        </React.Fragment>
    )}
</Timer>
                        {props.children}
                    </p>
                </div>
                <div className="modal-footer">
                    <button className="btn-cancel" onClick={props.close}>Cancel</button>
                    <button className="btn-continue" onClick={confirmSite}>Confirm</button>
                </div>
            </div>
        </div>
    )

}

export default Modal;
