import React, { Component } from 'react';
import  ImageMapper from 'react-image-mapper';
import  MaleBody  from '/Users/Danny/Documents/student-portal2/src/images/MaleBody.jpg';
import Modal from '../modal/Modal';

class Body extends Component {
	constructor() {
        super();

        this.state = {
			isShowing: false,
			preFillColor: 'green'
        }
    }

    openModalHandler = () => {
        this.setState({
            isShowing: true
        });
    }

    closeModalHandler = () => {
        this.setState({
            isShowing: false
        });
	}

	beginSiteTracker = () => {

		this.setState({ prefillColor: "green"



		});
	}

	enterArea(area) {
		this.setState({ hoveredArea: area });
		this.setState({preFillColor: "green"})
	}

	leaveArea(area) {
		this.setState({ hoveredArea: null });
	}

	getTipPosition(area) {
		return { top: `${area.center[1]}px`, left: `${area.center[0]}px` };
	}
    render() {



       let  MAP ={
            name: "my-map",
            areas: [
              { name: "5", shape: "circle", coords: [136, 181, 5 ], preFillColor: ""   },
            ]
          }


		  return (
			<div>

				<Modal
				className="modal"
				show={this.state.isShowing}
				close={this.closeModalHandler}

				>
					Do you want to confirm this as your current infusion site location?

			</Modal>




			<ImageMapper src={MaleBody} map={MAP} width={610} height={435} 
	button className="open-modal-btn" 

	onClick={area => this.beginSiteTracker(area)}
    >
		{ this.state.isShowing ? <div onClick={this.closeModalHandler} className="back-drop"></div> : null }

			</ImageMapper>




	</div>



);
}
}


export default Body;
