import React from 'react';
import './Modal.css';
import { Timer } from 'react-compound-timer';




const TimerModal = (props) => {

    infusionTimer = <Timer
	initialTime={10000}
	direction="backward"
	checkpoints={[
        {
            time: 10000 - 10000,
            callback: () => console.log('Checkpoint A'),
        }

    ]}
	>
	{() => (

	<React.Fragment>
		Time left for this site:<br></br>
		<Timer.Days /> days <br></br>
		<Timer.Hours /> hours<br></br>
		<Timer.Minutes /> minutes<br></br>
		<Timer.Seconds /> seconds<br></br>
	</React.Fragment>
	)}
	</Timer>



    return (

        <div>
            <div className="modal-wrapper"
                style={{
                    transform: props.show ? 'translateY(0vh)' : 'translateY(-100vh)',
                    opacity: props.show ? '1' : '0'
                }}>
                <div className="modal-header">
                    <h3>Infusion Tracker</h3>
                    <span className="close-modal-btn" onClick={props.close}>Ã—</span>
                </div>
                <div className="modal-body">
                    <p>
                        Time left for this infusion site:

                    </p>
                </div>
                <div className="modal-footer">
                    <button className="btn-primary" onClick={props.close}>Close</button>
                </div>
            </div>
        </div>
    )

}

export default TimerModal;
