import React from 'react';
import './Modal.css';
import axios from 'axios';



const Modal = (props) => {


    const email = localStorage.getItem("loggedInStudent");
    console.log("area ::> ", email)
    axios.get(`http://localhost:8080/sites/findSites/${email}`)
        .then(response => {
            console.log(response.data)
            let areasFromDb = response.data
                .map((record, index) => {
                    return {
                        name: index,
                        shape: record.shape,
                        coords: [record.xCoord, record.yCoord, 5],
                        fillColor: record.fillColor,
                        preFillColor: record.fillColor,
                        date: record.dateStarted,
                        savedArea: true
                    }
                })
            this.setState({ areas: areasFromDb })
        }).catch(error => {
        })

    let cancel = (event) => {
        props.removeLastAddedArea()
    }


    let confirmSite = (event) => {
        props.beginSiteTracker()
        props.areasSubmitHandler()
        props.close()

    };
    console.log("Timer props -> ", props.selectedArea.date)
    let initialDate  = new Date(props.selectedArea.date)
    let expireDate = new Date(initialDate)
    console.log("expire date ::> ", expireDate)



    let modalMessage = (
        <div></div>

    ) 
    if (this.props.state.savedArea=true){
        console.log(props.selectedArea.savedArea)
        console.log(props.selectedArea)
        modalMessage=(
            <div>
            <div className="modal-wrapper"
                style={{
                    transform: props.show ? 'translateY(0vh)' : 'translateY(-100vh)',
                    opacity: props.show ? '1' : '0'
                }}>
                <div className="modal-header">
                    <h3>Infusion Tracker</h3>
                    <span className="close-modal-btn" onClick={cancel}>Ã—</span>
                </div>

                 <div className="modal-body">
                    <p>This site expires on:</p>
                {expireDate.getMonth()}/{expireDate.getDate()+3} 


                 </div> 
                <div className="modal-footer">
                <button className="btn-cancel" onClick={props.close}>Close</button>
                 </div>
                 </div>
             </div>

        )

    }
    if(props.selectedArea.savedArea=false)
    {
        modalMessage =( 
            <div>
                <div className="modal-wrapper"
                style={{
                    transform: props.show ? 'translateY(0vh)' : 'translateY(-100vh)',
                    opacity: props.show ? '1' : '0'
                }}></div>
        <div className="modal-body">
                <p>
                    {props.children}
                    Do you want to confirm this as your current infusion site?
                </p>
                <div className="modal-header">
                    <h3>Infusion Tracker</h3>

                </div>
            </div>
            <div className="modal-footer">
                <button className="btn-cancel" onClick={cancel}>Cancel</button>
                <button className="btn-continue" onClick={confirmSite}>Confirm</button>
            </div>
            </div>
        )
    }





    return (
        <div>

                {modalMessage}


            </div>

    )

}

export default Modal;
