import React, { Component } from 'react';
import  ImageMapper from 'react-image-mapper';
import  MaleBody  from '/Users/Danny/Documents/student-portal2/src/images/MaleBody.jpg';
import Modal from '../modal/Modal';

class Body extends Component {
	constructor() {
        super();


    }

	toggleModal = () => {
		this.setState(prevState=> {
		isShowing: !prevState.isShowing
		})
		}

	beginSiteTracker = () => {

		this.setState({ fillColor: ""


		});
	}


    render() {



       let  MAP ={
            name: "my-map",
            areas: [
              { name: "5", shape: "circle", coords: [136, 181, 5 ], preFillColor: "blue"   },
            ]
          }


		  return (
			<div>

				<Modal
				className="modal"
				show={this.state.isShowing}
				close={this.toggleModal}

				>
					Do you want to confirm this as your current infusion site location?

			</Modal>




			<ImageMapper src={MaleBody} map={MAP} width={610} height={435} 
	button className="open-modal-btn" 
	onClick={this.openModalHandler}
    >
		{ this.state.isShowing ? <div onClick={this.closeModalHandler} className="back-drop"></div> : null }

			</ImageMapper>




	</div>



);
}
}


export default Body;
